import { createApp } from 'vue'
import Toast from "vue-toast-notification";
import 'vue-toast-notification/dist/theme-default.css'; // 或者你可以选择 'vue-toast-notification/dist/theme-sugar.css'
import App from './App.vue'

const app = createApp(App);

app.use(Toast);
app.mount('#app');
